import React, {useEffect, useState} from 'react'
import axios from 'axios'

import * as S from './styles/HomeStyles'


const Home = () => {
    /* const [heros, setHeros] = useState([]) */
    const [heroi, setHeroi] = useState([])
    const [id, setId] = useState()




    async function fetchData(search) {
         let response = await axios(`https://www.superheroapi.com/api.php/3992681667526682/search/${search}`)
        await setHeroi(response.data.results)        
        //await console.log(`Search - ${search} -- Response - ${heroi}`)
        //await renderData()  
    } 



    const handleHeroi = (e) => {
        fetchData(e.target.value)        
    }

    const handleBotao = () => {
        console.log(heroi)
    }

    return(
        <>
        <h1>Heros</h1>



        <form>
            <input type='text' onChange={handleHeroi} name='inputs' />
            <button label={'botao'} onClick={handleBotao}>
                Botao
            </button>
        </form>

        <S.card>
            {heroi ? heroi.map((hero) => 
            <>
                <S.item>
                    <S.imgHero src={hero.image.url} />

                    <ul>

                            <li>
                                <h3>id: {hero.id} - Nome: {hero.name}</h3>                    
                            </li>
                            <li>
                                <h2>Powerstats</h2>
                                <ul>
                                    <li>Intelligence = {hero.powerstats.intelligence}</li>
                                    <li>Strength = {hero.powerstats.strength}</li>
                                    <li>Speed = {hero.powerstats.speed}</li>
                                    <li>Durability = {hero.powerstats.durability}</li>
                                    <li>Combat = {hero.powerstats.combat}</li>

                                </ul>
                            </li>

                    </ul>
                </S.item>
            </>
            ) : ''}            
        </S.card>






        </>
    )
}

export default Home
