import React, {useState} from 'react'
import axios from 'axios'
import LinearProgress from '@material-ui/core/LinearProgress'
import { withStyles } from '@material-ui/core/styles'
import * as S from './styles/HomeStyles'
import ReactLoading from 'react-loading'
import Radio from '@material-ui/core/Radio'
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormControl from '@material-ui/core/FormControl';
import TextField from '@material-ui/core/TextField';
import FormLabel from '@material-ui/core/FormLabel'

import {     
    makeStyles,    
  } from '@material-ui/core/styles'



  const useStyles = makeStyles((theme) => ({    
    margin: {                      
        width: '50%',        
        borderRadius: 5,
        border: '1px solid black',
        height: '3.5rem',    
        backgroundColor: 'red',        
        boxShadow: '0 0 15px red',
        textAlign: 'center',
        fontSize: '1.5rem',
        zIndex: 5,


        [theme.breakpoints.up('sm')]: {
            width: '30%',
          },
    },    

    headText:{
        width: '20%',
        fontSize: '1rem',
        textShadow: '1px 1px 5px white',
        textalign: 'center',

        [theme.breakpoints.up('sm')]: {
            width: '30%',
            fontSize: '1.3rem',
            textShadow: '3px 1px 5px white',
          },

        transition: theme.transitions.create(['border-color', 'box-shadow'])
    }
  }));





const Home = () => {
    /* const [heros, setHeros] = useState([]) */
    const [heroi, setHeroi] = useState([])
    const [load, setLoad] = useState('none')
    const classes = useStyles()
    const [displayText, setDisplayText] = useState('flex')


    const BorderLinearProgress = withStyles((theme) => ({
        root: {
          height: 15,
          borderRadius: 5,
        },
        colorPrimary: {
          backgroundColor: 'grey',
        },
        bar: {
          borderRadius: 5,
          backgroundColor: 'red',
        },
      }))(LinearProgress);


    async function fetchData(search) {
        setLoad('block')
         let response = await axios(`https://www.superheroapi.com/api.php/3992681667526682/search/${search}`)
        await setHeroi(response.data.results)        
        await setLoad('none')
        //await console.log(`Search - ${search} -- Response - ${heroi}`)            
    } 



    const handleHeroi = (e) => {
        let searchHero = e.target.value
        setDisplayText('none')
        if(searchHero.length >= 3){
            fetchData(searchHero)  
        }

        if(searchHero.length === 0){
            fetchData(searchHero)  
            setDisplayText('flex')
        }

    }

    const handleChange = (e) => {
        console.log(e.target.value)
    }




    return(
        <S.container>

            <S.card>
                <S.divText style={{display: displayText}}>
                    <div>
                        <S.spanSearch >Encontre seu Super-Herói Favorito</S.spanSearch>
                    </div>

                    <div>
                        <S.spanSearch>Digite pelo menos 3 letras</S.spanSearch>
                    </div>
                </S.divText>

                <S.loader style={{display: load}}>
                    <ReactLoading type={'cylon'} color={'red'} height={367} width={175}  />

                </S.loader>

                <S.divSearch>

                    <TextField
                            className={classes.margin}
                            label="Search"
                            variant="outlined"
                        // id="mui-theme-provider-outlined-input"
                            onChange={handleHeroi}
                    />

                </S.divSearch>

                {heroi ? heroi.map((hero) => 
                <>
                    <S.item>
                        <S.imgHero src={hero.image.url} />

                        <S.listUl>

                                <li>
                                    <h3>ID: {hero.id} - Nome: {hero.name}</h3>                    
                                </li>
                                <S.data>

                                    <ul>

                                    <FormControl component="fieldset">
                                    <FormLabel component="legend">Powerstats</FormLabel>
                                    <RadioGroup aria-label="gender" name="gender1" onChange={handleChange}>
                                        <div>
                                        <FormControlLabel value={hero.powerstats.intelligence} control={<Radio />} label="Intelligence" />
                                            <BorderLinearProgress variant='determinate' value={hero.powerstats.intelligence}/> 
                                            <S.spanBar >{hero.powerstats.intelligence}%</S.spanBar>
                                        </div>
                                        <FormControlLabel value={hero.powerstats.strength} control={<Radio />} label="Strength" />
                                        <FormControlLabel value={hero.powerstats.speed} control={<Radio />} label="Speed" />
                                        <FormControlLabel value={hero.powerstats.durability} control={<Radio />} label="Durability" />
                                        <FormControlLabel value={hero.powerstats.combat} control={<Radio />} label="Combat" />
                                    </RadioGroup>
                                    </FormControl>

                                        {/* <S.liItem style={{display:'block'}}>Intelligence: <br/>
                                            <div style={{display:'flex'}}>
                                            <Radio
                                                //checked={selectedValue === 'a'}
                                                onChange={handleChange}
                                                value={hero.powerstats.intelligence}
                                                name="intelligence"
                                               // inputProps={{ 'aria-label': 'A' }}
                                            />
                                            <div>
                                            <BorderLinearProgress variant='determinate' value={hero.powerstats.intelligence}/> 
                                            <S.spanBar >{hero.powerstats.intelligence}%</S.spanBar>
                                            </div>
                                            </div>
                                        </S.liItem>

                                        <S.liItem>Strength: <br/> 
                                            <Radio
                                                    //checked={selectedValue === 'a'}
                                                    onChange={handleChange}
                                                    value={hero.powerstats.strength}
                                                    name="intelligence"
                                                // inputProps={{ 'aria-label': 'A' }}
                                            />
                                            <BorderLinearProgress variant='determinate' value={hero.powerstats.strength}/>
                                            <S.spanBar >{hero.powerstats.strength}%</S.spanBar>
                                        </S.liItem>

                                        <S.liItem>Speed: <br/> 
                                            <Radio
                                                    //checked={selectedValue === 'a'}
                                                    onChange={handleChange}
                                                    value={hero.powerstats.speed}
                                                    name="intelligence"
                                                // inputProps={{ 'aria-label': 'A' }}
                                            />
                                            <BorderLinearProgress variant='determinate' value={hero.powerstats.speed}/>   
                                            <S.spanBar >{hero.powerstats.speed}%</S.spanBar>                                 
                                        </S.liItem>

                                        <S.liItem>Durability: <br/> 
                                            <Radio
                                                    //checked={selectedValue === 'a'}
                                                    onChange={handleChange}
                                                    value={hero.powerstats.durability}
                                                    name="intelligence"
                                                // inputProps={{ 'aria-label': 'A' }}
                                            />
                                            <BorderLinearProgress variant='determinate' value={hero.powerstats.durability}/>
                                            <S.spanBar >{hero.powerstats.durability}%</S.spanBar>
                                        </S.liItem>

                                        <S.liItem>Combat: <br/> 
                                            <Radio
                                                    //checked={selectedValue === 'a'}
                                                    onChange={handleChange}
                                                    value={hero.powerstats.combat}
                                                    name="intelligence"
                                                // inputProps={{ 'aria-label': 'A' }}
                                            />
                                            <BorderLinearProgress variant='determinate' value={hero.powerstats.combat}/>
                                            <S.spanBar >{hero.powerstats.combat}%</S.spanBar>
                                        </S.liItem> */}

                                    </ul>
                                </S.data>



                        </S.listUl>
                    </S.item>
                </>
                ) : ''}  

            </S.card>                            

        </S.container>
    )
}

export default Home
